<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Media;

/**
 * voteRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class VoteRepository extends \Doctrine\ORM\EntityRepository
{
    public function findMediaTrends()
    {
        $em  = $this->getEntityManager();
        $dql = 'SELECT DISTINCT m.title, m.slug, m.image
                           FROM AppBundle:Vote v
                           JOIN v.media m
                          WHERE v.rating > 2';

        $query = $em->createQuery($dql);
        $query->setMaxResults(Media::NUM_ITEMS);
        $query->execute();
        return $query->getResult();
    }

    public function findVoteIfExist($id, $user)
    {
        $em = $this->getEntityManager();
        $dql = 'SELECT v
                  FROM AppBundle:Vote v
                 WHERE v.media = :media
                   AND v.user = :user';

        $query = $em->createQuery($dql);
        $query->setParameter('media', $id);
        $query->setParameter('user', $user);

        return $query->getOneOrNullResult();
    }
}
